    private HashMap<String, String> usuarios = new HashMap<>();

    public void anadirUsuario(String nombre, String contrasena) {
        usuarios.put(nombre, contrasena);
        
    }

    public boolean buscarUsuario(String nombre, String contrasena) {
        if (usuarios.containsKey(nombre)) {
            String password = usuarios.get(nombre);
            return password != null && password.equals(contrasena);
        }
        return false;
    }

